import { useEffect, useRef, useState } from 'react';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const ws = useRef(null);
  const pc = useRef(null);
  const localVideo = useRef();
  const remoteVideo = useRef();

  useEffect(() => {
    ws.current = new WebSocket('ws://localhost:8080');

    ws.current.onmessage = async (event) => {
      const data = JSON.parse(event.data);

      if (data.type === 'info') {
        setMessages((prev) => [...prev, `ðŸ›ˆ ${data.message}`]);
      } else if (data.type === 'message') {
        setMessages((prev) => [...prev, `Stranger: ${data.message}`]);
      } else if (data.type === 'match') {
        await startPeer(true);
      } else if (data.type === 'offer') {
        await startPeer(false, data.offer);
      } else if (data.type === 'answer') {
        await pc.current.setRemoteDescription(data.answer);
      } else if (data.type === 'candidate') {
        await pc.current.addIceCandidate(data.candidate);
      }
    };

    navigator.mediaDevices.getUserMedia({ video: true, audio: true })
      .then(stream => {
        localVideo.current.srcObject = stream;
      });

    return () => {
      ws.current.close();
    };
  }, []);

  const startPeer = async (initiator = true, remoteOffer = null) => {
    pc.current = new RTCPeerConnection();

    pc.current.onicecandidate = (e) => {
      if (e.candidate) {
        ws.current.send(JSON.stringify({ type: 'candidate', candidate: e.candidate }));
      }
    };

    pc.current.ontrack = (e) => {
      remoteVideo.current.srcObject = e.streams[0];
    };

    const stream = localVideo.current.srcObject;
    stream.getTracks().forEach(track => pc.current.addTrack(track, stream));

    if (initiator) {
      const offer = await pc.current.createOffer();
      await pc.current.setLocalDescription(offer);
      ws.current.send(JSON.stringify({ type: 'offer', offer }));
    } else {
      await pc.current.setRemoteDescription(remoteOffer);
      const answer = await pc.current.createAnswer();
      await pc.current.setLocalDescription(answer);
      ws.current.send(JSON.stringify({ type: 'answer', answer }));
    }
  };

  const sendMessage = () => {
    if (input.trim() !== '') {
      ws.current.send(JSON.stringify({ type: 'text', text: input }));
      setMessages((prev) => [...prev, `You: ${input}`]);
      setInput('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') sendMessage();
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <div style={{ display: 'flex', gap: '10px', margin: '20px' }}>
        <video ref={localVideo} autoPlay muted style={{ width: '300px', height: '200px', background: 'black' }} />
        <video ref={remoteVideo} autoPlay style={{ width: '300px', height: '200px', background: 'black' }} />
      </div>

      <div style={{ width: '400px', height: '200px', overflowY: 'auto', border: '1px solid #ccc', marginBottom: '10px', padding: '10px' }}>
        {messages.map((msg, idx) => <div key={idx}>{msg}</div>)}
      </div>

      <div>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type a message..."
          style={{ padding: '10px', width: '300px' }}
        />
        <button onClick={sendMessage} style={{ padding: '10px' }}>Send</button>
      </div>
    </div>
  );
}

export default App;
